mapper.select:
select * from blog.t_journal


        id = #{id}


        and operate_name = #{operateName}


        and success = #{success}


        and request_ip = #{requestIp}


        and request_class_name = #{requestClassName}


        and request_method_name = #{requestMethodName}


        and request_url = #{requestUrl}

mapper.insert:
insert into blog.t_journal(id, operate_name, success, request_ip, request_class_name, request_method_name, request_url) value (
id = #{id}, operate_name = #{operateName}, success = #{success}, request_ip = ${requestIp}, request_class_name = #{requestClassName}, request_method_name = #{requestMethodName}, request_url = #{requestUrl}
)
